#!/usr/bin/env node
const argv = require('yargs')
    .option('port', {
        alias: 'p',
        describe: 'Specify the port number',
        default: 7860
    })
    .option('uuid', {
        alias: 'u',
        describe: 'Specify the uuid',
        default: '4dacb1b4-44c5-456b-b3a4-6a2def7fe0c9'
    })
    .help()
    .argv;

// 通过参数启动你的应用
require('./index.js')(argv.port, argv.uuid);
